import React from "react";
import { withStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import Typography from '@material-ui/core/Typography';
import Divider from '@material-ui/core/Divider';
import Checkbox from '@material-ui/core/Checkbox';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import { FormControl } from "@material-ui/core";
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemSecondaryAction from '@material-ui/core/ListItemSecondaryAction';
import ListItemText from '@material-ui/core/ListItemText';
import IconButton from '@material-ui/core/IconButton';
import CommentIcon from '@material-ui/icons/Comment';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';

const StyledCard = withStyles({
    root: {
        padding: '0, 10px',
        marginTop: '20px',
    },
})(Card);

const StyledListItemIcon = withStyles({
    root: {
        minWidth: '0px',
        fontSize: '0.5rem'
    },
})(ListItemIcon);

const StyledListItem = withStyles({
    root: {
        padding: '0px',
    },
})(ListItem);
export function getFilterValueDisplay(filterValue) {
    if (filterValue === undefined || filterValue === null) return "";
    if (filterValue.hasOwnProperty("name")) return filterValue.name;
    return String(filterValue);
}

export default function ({ className,
    label,
    onMoreClick,
    onRemove,
    onSelect,
    options,
    showMore,
    showSearch,
    onSearch,
    searchPlaceholder }) {
    //const classes = useStyles();
    //const fields = getEscapedFields(result);
    console.log("filter")
    return (
        <StyledCard>
            <CardContent>
                <Typography variant="h6" component="h3" color="primary">
                    {label}
                    <Divider />

                </Typography>

                <List>
                    {options.length < 1 && <div>No matching options</div>}
                    {options.map(option => {
                        const checked = option.selected;
                        return (
                            <StyledListItem button>
                                <StyledListItemIcon>
                                    <Checkbox size="small" color="primary"
                                        id={`example_facet_${label}${getFilterValueDisplay(
                                            option.value
                                        )}`}
                                        checked={checked}
                                        onChange={() =>
                                            checked ? onRemove(option.value) : onSelect(option.value)
                                        }
                                    />
                                </StyledListItemIcon>
                                <ListItemText multiline primary={getFilterValueDisplay(option.value)} />
                                <ListItemText secondary={option.count.toLocaleString("en")} />
                            </StyledListItem>
                        );
                    })}
                </List>
            </CardContent>
                    <CardActions>
                    {
                        showMore && (
                            <Button
                                type="button"
                                className="sui-facet-view-more"
                                onClick={onMoreClick}
                                aria-label="Show more options"
                            >
                                + More
                            </Button>
                        )
                    }
                    {showSearch && (
                        <TextField id="standard-search" label="Filter" type="search" onChange={e => {
                            onSearch(e.target.value);
                        }} />
                    )}

                    </CardActions>


        </StyledCard>
    );
}
