import React from "react";
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';

const useStyles = makeStyles({
    root: {
        minWidth: 275,
    },
    bullet: {
        display: 'inline-block',
        margin: '0 2px',
        transform: 'scale(0.8)',
    },
    title: {
        fontSize: 14,
    },
    pos: {
        marginBottom: 12,
    },
});

export default ({ result }) => {
    const classes = useStyles();
    const bull = <span className={classes.bullet}>•</span>;

    return (
    <li className="sui-result">
        <Card> 
            <CardContent>
                <Typography color="textSecondary" gutterBottom>
                    Word of the Day
        </Typography>
                <Typography variant="h5" component="h2">

        </Typography>
                <Typography  color="textSecondary">
                    adjective
        </Typography>
                <Typography variant="body2" component="p">
                    well meaning and kindly.
          <br />
                    {'"a benevolent smile"'}
                </Typography>
            </CardContent>
            <CardActions>
                <Button size="small">Learn More</Button>
            </CardActions>
        </Card>
        <div className="sui-result__header">
            <span
                className="sui-result__title"
                // Snippeted results contain search term highlights with html and are
                // 100% safe and santitized, so we dangerously set them here
                dangerouslySetInnerHTML={{ __html: result.domain.snippet}}
            />
        </div>
        <div className="sui-result__body">
            <div
                className="sui-result__image"
                style={{
                    maxWidth: "140px",
                    paddingLeft: "24px",
                    paddingTop: "10px"
                }}
            >
                <img
                    src={result.domain.raw}
                    alt="thumb"
                    style={{
                        display: "block",
                        width: "100%",
                        height: "100%",
                        objectFit: "cover",
                        objectPosition: "center"
                    }}
                />
            </div>
            <ul className="sui-result__details">
                <li>
                    <span className="sui-result__key">Genre</span>{" "}
                    <span
                        className="sui-result__value"
                        dangerouslySetInnerHTML={{
                            __html: result.domain.snippet
                        }}
                    />
                </li>
                <li>
                    <span className="sui-result__key">Publisher</span>{" "}
                    <span
                        className="sui-result__value"
                        dangerouslySetInnerHTML={{
                            __html: result.domain.snippet
                        }}
                    />
                </li>
                <li>
                    <span className="sui-result__key">Critic Score</span>{" "}
                    <span className="sui-result__value">{result.domain.raw}</span>
                </li>
                <li>
                    <span className="sui-result__key">User Score</span>{" "}
                    <span className="sui-result__value">{result.domain.raw}</span>
                </li>
                <li>
                    <span className="sui-result__key">Platform</span>{" "}
                    <span
                        className="sui-result__value"
                        dangerouslySetInnerHTML={{
                            __html: result.domain.snippet
                        }}
                    />
                </li>
            </ul>
        </div>
    </li>
);

                    }
