import React from "react";
import { makeStyles } from '@material-ui/core/styles';
import CardActionArea from '@material-ui/core/CardActionArea';
import Card from '@material-ui/core/Card';
import CardHeader from '@material-ui/core/CardHeader';
import CardMedia from '@material-ui/core/CardMedia';
import CardContent from '@material-ui/core/CardContent';
import CardActions from '@material-ui/core/CardActions';
import Collapse from '@material-ui/core/Collapse';
import Avatar from '@material-ui/core/Avatar';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import { red } from '@material-ui/core/colors';
import FavoriteIcon from '@material-ui/icons/Favorite';
import { MailOutline, DoneOutline, Info, DeveloperMode, Equalizer, Home, Description } from '@material-ui/icons';
import ShareIcon from '@material-ui/icons/Share';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import MoreVertIcon from '@material-ui/icons/MoreVert';
import green from '@material-ui/core/colors/green';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import InboxIcon from '@material-ui/icons/Inbox';
import Dns from '@material-ui/icons/Dns';
import Divider from '@material-ui/core/Divider';

const useStyles = makeStyles({
    root: {
        minWidth: 275,
    },
    bullet: {
        display: 'inline-block',
        margin: '0 2px',
        transform: 'scale(0.8)',
    },
    title: {
        fontSize: 14,
    },
    pos: {
        marginBottom: 12,
    },
});

function Valid() {
    //console.log("Test")
}
function getFieldType(result, field, type) {
    if (result[field]) return result[field][type];
}

function getRaw(result, field) {
    return getFieldType(result, field, "raw");
}

function getSnippet(result, field) {
    return getFieldType(result, field, "snippet");
}
function htmlEscape(str) {
    if (!str) return "";

    return String(str)
        .replace(/&/g, "&amp;")
        //.replace(/"/g, "&quot;")
        .replace(/'/g, "&#39;")
        .replace(/</g, "&lt;")
        .replace(/>/g, "&gt;");
}

export function isFieldValueWrapper(object) {
    //console.log(object)
    return (
        object && (object.hasOwnProperty("raw") || object.hasOwnProperty("snippet") && !object.hasOwnProperty("score"))
    );
}

function getEscapedField(result, field) {
    // Fallback to raw values here, because non-string fields
    // will not have a snippet fallback. Raw values MUST be html escaped.
    let safeField =
        htmlEscape(getRaw(result, field));
    console.log(field)
    safeField = safeField.replaceAll(",", "\n");
    return Array.isArray(safeField) ? safeField.join(",") : safeField;
}

function getEscapedFields(result) {
    return Object.keys(result).reduce((acc, field) => {
        // If we receive an arbitrary value from the response, we may not properly
        // handle it, so we should filter out arbitrary values here.
        //
        // I.e.,
        // Arbitrary value: "_metaField: '1939191'"
        // vs.
        // FieldValueWrapper: "_metaField: {raw: '1939191'}"
        //if (!isFieldValueWrapper(result[field])) return acc;
        //console.log(field)
        if(field === 'id') {
            //console.log("remove" + field)
            //return { ...acc, [field]: getEscapedField(result, field) };
        }
        console.log(getEscapedField(result, field))
        return { ...acc, [field]: getEscapedField(result, field) };

    }, {});
}

function generate(result, icon, field) {
    const DynamicIconButton = icon;
    return (
        <ListItem>
            <ListItemIcon>
                <Dns />
            </ListItemIcon>
            <ListItemText
                primary={result.ns}
            />
        </ListItem>
    );
}

export default function ({ result }) {
    //const classes = useStyles();
    //console.log(result)
    const fields = getEscapedFields(result);
    console.log(fields)
    const green = '#00a152';
    //console.log(result)
    return (
        <>
            <Card>
                <CardActionArea>
                    <CardContent>
                        <Typography gutterBottom variant="h5" component="h2" color="primary">
                            {fields.domain}
                        </Typography>
                        <Divider />
                        <Typography variant="body2" color="textPrimary" style={{ whiteSpace: 'pre-line' }} gutterBottom component="a">
                            <List>
                                {(generate(fields, "Dns", "ns"))}
                                {(generate(fields, "Dns"))}
                                {(generate(fields, "Dns"))}
                                {(generate(fields, "Dns"))}
                                {(generate(fields, "Dns"))}
                                {(generate(fields, "Dns"))}
                                {(generate(fields, "Dns"))}
                                {(generate(fields, "Dns"))}
                                <ListItem>
                                    <ListItemIcon>
                                        <Dns />
                                    </ListItemIcon>
                                    <ListItemText
                                        primary={fields.ns}
                                    />
                                </ListItem>
                                <ListItem>
                                    <ListItemIcon>
                                        <Dns />
                                    </ListItemIcon>
                                    <ListItemText
                                        primary={fields.a}
                                    />
                                </ListItem>
                                <ListItem>
                                    <ListItemIcon>
                                        <Dns />
                                    </ListItemIcon>
                                    <ListItemText
                                        primary={fields.aaaa}
                                    />
                                </ListItem>
                                <ListItem>
                                    <ListItemIcon>
                                        <Dns />
                                    </ListItemIcon>
                                    <ListItemText
                                        primary={fields.mx}
                                    />
                                </ListItem>
                                <ListItem>
                                    <ListItemIcon>
                                        <Dns />
                                    </ListItemIcon>
                                    <ListItemText
                                        primary={fields.aaaa}
                                    />
                                </ListItem>
                            </List>
                        </Typography>
                    </CardContent>
                </CardActionArea>
                <Divider />
                <CardActions disableSpacing>
                    <IconButton disabled={result?.valid_info?.raw === 'true' ? "inherit" : "secondary"} aria-label="add to favorites" >
                        <MailOutline />
                        {Valid()}
                    </IconButton>
                    <IconButton color={result?.valid_domain?.raw === 'true' ? "inherit" : "secondary"} aria-label="share" >
                        <DoneOutline />

                    </IconButton>
                </CardActions>
            </Card>

        </>
    );

}
