import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import InputBase from '@material-ui/core/InputBase';
import Divider from '@material-ui/core/Divider';
import IconButton from '@material-ui/core/IconButton';
import MenuIcon from '@material-ui/icons/Menu';
import SearchIcon from '@material-ui/icons/Search';
import DirectionsIcon from '@material-ui/icons/Directions';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import SearchBar from "material-ui-search-bar";
import {
    ErrorBoundary,
    Facet,
    SearchProvider,
    SearchBox,
    Results,
    PagingInfo,
    ResultsPerPage,
    Paging,
    Sorting,
    WithSearch
} from "@elastic/react-search-ui";

const useStyles = makeStyles((theme) => ({
  root: {
    padding: '2px 4px',
    display: 'flex',
    alignItems: 'center',
    width: 800,
  },
  input: {
    marginLeft: theme.spacing(1),
    flex: 1,
    width: 600,
    },
  form: {
    width: 600,
    },
  iconButton: {
    padding: 10,
    float: right
  },
  divider: {
    height: 28,
    margin: 4,
  },
}));

    export default function CustomizedInputBase() {
        const classes = useStyles();
        return (
            <Paper component="form" className={classes.root}>
                <SearchBox className={classes.form}
                //autocompleteSuggestions={true}
                inputView={({ getAutocomplete, getInputProps, getButtonProps }) => (
                    <>
                        <InputBase 
                            className={classes.input} 
                            //inputProps={{ 'aria-label': 'Search .ch zonefile' }} 
                            {...getInputProps({ placeholder: 'Search .ch zonefile' })} />
                            {getAutocomplete()}
                        <IconButton {...getButtonProps()} type="submit" className={classes.iconButton} aria-label="search" >
                            <SearchIcon />
                        </IconButton>
                    </>
                )}
            />
            </Paper>

    );
}
