import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import InputBase from '@material-ui/core/InputBase';
import IconButton from '@material-ui/core/IconButton';
import SearchIcon from '@material-ui/icons/Search';
import Box from '@material-ui/core/Box';
import InputAdornment from "@material-ui/core/InputAdornment";
import {
    SearchBox,
} from "@elastic/react-search-ui";

const useStyles = makeStyles((theme) => ({
    box: {
        justifyContent: "center",
        width: "100%",
    },
    paper: {
        margin: '30px',
        padding: '4px 4px',
        display: 'flex',
        width: "60%",
    },
    input: {
        padding: '40px',
    },
    iconButton: {
        padding: 10,

    },
}));

export default function CustomizedInputBase() {
    const classes = useStyles();
    return (
        <Box display="flex" className={classes.box}>
            <Paper className={classes.paper}>

                <SearchBox className={classes.form}
                    //autocompleteSuggestions={true}
                    inputView={({ getAutocomplete, getInputProps, getButtonProps }) => (
                        <>
                            <InputBase autoFocus className={classes.input}
                                {...getInputProps({ placeholder: 'Search .ch zonefile' })} />
                            <IconButton {...getButtonProps()} type="submit" className={classes.iconButton} aria-label="search" >
                                <SearchIcon />
                            </IconButton>
                        </>
                    )}
                />
            </Paper>
        </Box>

    );
}
