import React from "react";
import AppSearchAPIConnector from "@elastic/search-ui-app-search-connector";
import PacmanLoader from "react-spinners/ScaleLoader";
import {
    ErrorBoundary,
    Facet,
    SearchProvider,
    SearchBox,
    Results,
    PagingInfo,
    ResultsPerPage,
    Paging,
    Sorting,
    WithSearch
} from "@elastic/react-search-ui";
import CssBaseline from '@material-ui/core/CssBaseline';
import { Layout } from "@elastic/react-search-ui-views";
//import "@elastic/react-search-ui-views/lib/styles/styles.css";
import {
    buildAutocompleteQueryConfig,
    buildFacetConfigFromConfig,
    buildSearchOptionsFromConfig,
    buildSortOptionsFromConfig,
    getConfig,
    getFacetFields
} from "../config/config-helper";
import Button from '@material-ui/core/Button';
import SearchBar from "material-ui-search-bar";
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import Input from '@material-ui/core/Input';
import { fade, makeStyles } from '@material-ui/core/styles';
import MenuIcon from '@material-ui/icons/Menu';
import SearchIcon from '@material-ui/icons/Search';
import TextField from '@material-ui/core/TextField';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import Divider from '@material-ui/core/Divider';
import DirectionsIcon from '@material-ui/icons/Directions';

const { hostIdentifier, searchKey, endpointBase, engineName } = getConfig();
const connector = new AppSearchAPIConnector({
    searchKey,
    engineName,
    hostIdentifier,
    endpointBase
});
const config = {
    searchQuery: {
        facets: buildFacetConfigFromConfig(),
        ...buildSearchOptionsFromConfig()
    },
    autocompleteQuery: buildAutocompleteQueryConfig(),
    apiConnector: connector,
    alwaysSearchOnInitialLoad: true,
    initialState: {
        resultsPerPage: 5
    }
};

export default function Home() {
    return (
        <React.Fragment>
            <CssBaseline />
            <SearchProvider config={config}>
                <WithSearch mapContextToProps={({ wasSearched, isLoading }) => ({ wasSearched, isLoading })}>
                    {({ wasSearched, isLoading }) => {
                        return (
                            <div className="App">
                                <ErrorBoundary>
                                    <Layout
                                        header={
                                            //<SearchBox autocompleteSuggestions={true} searchAsYouType={false} autocompleteMinimumCharacters={3} />
                                            //<CustomizedInputBase></CustomizedInputBase>

                                            // <SearchBox
                                            //     autocompleteSuggestions={true}
                                            //     inputView={({ getAutocomplete, getInputProps, getButtonProps }) => (
                                            //         <>
                                            //             <SearchBar
                                            //                 onRequestSearch={() => console.log('onRequestSearch')}
                                            //                 style={{
                                            //                     margin: '0 auto',
                                            //                     maxWidth: 800
                                            //                 }}
                                            //             />
                                            //             {getAutocomplete()}
                                            //         </>
                                            //     )}
                                            // />

                                            // <SearchBox autocompleteSuggestions={true}
                                            //     view={({ onChange, value, onSubmit }) => (
                                            //         <form onSubmit={onSubmit}>
                                            //             <SearchBar onChange={() => console.log('onChange')}
                                            //                 onRequestSearch={() => console.log('onRequestSearch')} />
                                            //         </form>
                                            //     )}
                                            //     />
                                            <SearchBox
                                            autocompleteSuggestions={true}
                                            inputView={({ getAutocomplete, getInputProps, getButtonProps }) => (
                                                <>
                                                    <Autocomplete
                                                        id="free-solo-demo"
                                                        freeSolo
                                                        options={top100Films.map((option) => option.title)}
                                                        renderInput={(params) => (
                                                            <TextField {...params} label="freeSolo" margin="normal" variant="outlined" />
                                                        )}
                                                    />
                                                    <TextField {...getInputProps({ placeholder: 'Search games' })} label="freeSolo" margin="normal" variant="outlined" />
                                                </>
                                            )}
                                            />
                                        }
                                        sideContent={
                                            <div>
                                                {!isLoading && wasSearched && (
                                                    <Sorting
                                                        label={"Sort by"}
                                                        sortOptions={buildSortOptionsFromConfig()}
                                                    />
                                                )}
                                                {getFacetFields().map(field => (
                                                    <Facet key={field} field={field} label={field} isFilterable={true} />
                                                ))}
                                            </div>
                                        }
                                        bodyContent={
                                            <div>

                                                {isLoading && (
                                                    <PacmanLoader size={100} color="blue" />
                                                )}
                                                {wasSearched && (
                                                    <Results
                                                        titleField={getConfig().titleField}
                                                        urlField={getConfig().urlField}
                                                        shouldTrackClickThrough={true}
                                                    />
                                                )}
                                            </div>
                                        }
                                        bodyHeader={
                                            <React.Fragment>
                                                {wasSearched && <PagingInfo />}
                                                {wasSearched && <ResultsPerPage options={[5, 10, 15]} />}
                                            </React.Fragment>
                                        }
                                        bodyFooter={<Paging />}
                                    />
                                </ErrorBoundary>
                            </div>
                        );
                    }}
                </WithSearch>
            </SearchProvider>
        </React.Fragment>
    );
}
