import React from "react";

import AppSearchAPIConnector from "@elastic/search-ui-app-search-connector";
import PacmanLoader from "react-spinners/ScaleLoader";
import {
  ErrorBoundary,
  Facet,
  SearchProvider,
  SearchBox,
  Results,
  PagingInfo,
  ResultsPerPage,
  Paging,
  Sorting,
  WithSearch
} from "@elastic/react-search-ui";
import { Layout } from "@elastic/react-search-ui-views";
import "@elastic/react-search-ui-views/lib/styles/styles.css";

import {
  buildAutocompleteQueryConfig,
  buildFacetConfigFromConfig,
  buildSearchOptionsFromConfig,
  buildSortOptionsFromConfig,
  getConfig,
  getFacetFields
} from "./config/config-helper";

const { hostIdentifier, searchKey, endpointBase, engineName } = getConfig();
const connector = new AppSearchAPIConnector({
  searchKey,
  engineName,
  hostIdentifier,
  endpointBase
});
const config = {
  searchQuery: {
    facets: buildFacetConfigFromConfig(),
    ...buildSearchOptionsFromConfig()
  },
  autocompleteQuery: buildAutocompleteQueryConfig(),
  apiConnector: connector,
  alwaysSearchOnInitialLoad: true
};

const PagingInfoView = ({ start, end, searchTerm }) => (
  <div className="paging-info">
    <strong>
      {start} - {end} - {searchTerm}
    </strong>
  </div>
);

const SearchBoxView = ({ onChange, searchTerm, onSubmit, autocompletedResults }) => (
  <div className="search-bar">
    <input value={searchTerm} onChange={e => onChange(e.currentTarget.value)} />
  </div>
);

export default function App() {
  return (
    <SearchProvider config={config}>
      <WithSearch
        mapContextToProps={({ searchTerm, setSearchTerm, results, isLoading }) => ({
          searchTerm,
          setSearchTerm,
          results,
          isLoading
        })}
      >
        {({ searchTerm, setSearchTerm, results, isLoading }) => {
          return (
            <div>
              <SearchBox view={SearchBoxView} />
              <input
                value={searchTerm}
                onChange={e => setSearchTerm(e.target.value)}
              />
              <div>
                {isLoading && (
                  <PacmanLoader size={100} color="blue" />
                )}
              </div>
              {results.map(r => (
                <div key={r.id}>{r.domain.raw}</div>
              ))}
              <PagingInfo view={PagingInfoView} />
            </div>
          );
        }}
      </WithSearch>
    </SearchProvider>
  );
}
