import React from 'react';

import PropTypes from "prop-types";
import setCurrent from "../../node_modules/@elastic/react-search-ui/"
import RCPagination from "rc-pagination";
import enUsLocale from "rc-pagination/lib/locale/en_US";

//import { appendClassName } from "./view-helpers";
import { makeStyles } from '@material-ui/core/styles';
import Pagination from '@material-ui/lab/Pagination';

// export function setCurrent(current) {
//     // eslint-disable-next-line no-console
//     if (this.debug) console.log("Search UI: Action", "setCurrent", ...arguments);

//     this._updateSearchResults({
//         current
//     });
// }

const useStyles = makeStyles((theme) => ({
    root: {
        '& > *': {
            marginTop: theme.spacing(2),
        },
    },
}));
function PagingView({
    className,
    current,
    resultsPerPage,
    onChange,
    totalPages,
    ...rest
}) {
    const [page, setPage] = React.useState(1);
    const handleChange = (event, value) => {
        setPage(value);
    };
    console.log(onChange)
    return (
        <Pagination page={current} count={totalPages} onChange={handleChange} color="primary">
        <RCPagination
            current={current}
            //onChange={onChange}
            onChange={setCurrent}
            pageSize={resultsPerPage}
            total={totalPages * resultsPerPage}
            //className={appendClassName("sui-paging", className)}
            locale={enUsLocale}
            {...rest}
        />
        </Pagination>
    );
}

PagingView.propTypes = {
    current: PropTypes.number.isRequired,
    onChange: PropTypes.func.isRequired,
    resultsPerPage: PropTypes.number.isRequired,
    totalPages: PropTypes.number.isRequired,
    className: PropTypes.string
};

export default PagingView;
