import React from "react";
import { withStyles } from '@material-ui/core/styles';
import CardActionArea from '@material-ui/core/CardActionArea';
import Card from '@material-ui/core/Card';
import CardHeader from '@material-ui/core/CardHeader';
import CardMedia from '@material-ui/core/CardMedia';
import CardContent from '@material-ui/core/CardContent';
import CardActions from '@material-ui/core/CardActions';
import Collapse from '@material-ui/core/Collapse';
import Avatar from '@material-ui/core/Avatar';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import { red } from '@material-ui/core/colors';
import FavoriteIcon from '@material-ui/icons/Favorite';
import ShareIcon from '@material-ui/icons/Share';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import MoreVertIcon from '@material-ui/icons/MoreVert';
import green from '@material-ui/core/colors/green';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import InboxIcon from '@material-ui/icons/Inbox';
import Dns from '@material-ui/icons/Dns';
import Divider from '@material-ui/core/Divider';
import { TextFields, VpnKey, MailOutline, VerifiedUser, AlternateEmail, Filter6, Filter4, DeleteForever } from '@material-ui/icons';
import Checkbox from '@material-ui/core/Checkbox';
import Paper from '@material-ui/core/Paper';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';



const StyledCard = withStyles({
    root: {
        padding: '0, 10px',
        marginTop: '20px',
    },
})(Card);

export function getFilterValueDisplay(filterValue) {
    if (filterValue === undefined || filterValue === null) return "";
    if (filterValue.hasOwnProperty("name")) return filterValue.name;
    return String(filterValue);
}

export default function ({ className,
    label,
    onMoreClick,
    onRemove,
    onSelect,
    options,
    showMore,
    showSearch,
    onSearch,
    searchPlaceholder }) {
    //const classes = useStyles();
    //const fields = getEscapedFields(result);
    console.log("filter")
    return (
        <StyledCard>
            <CardContent>
                <Typography variant="h5" component="h2" color="primary">
                    {label}
                    <Divider />
                    {showSearch && (
                        <div className="sui-facet-search">
                            <TextField id="standard-search" label="Search field" type="search" onChange={e => {
                                onSearch(e.target.value);
                            }} />
                        </div>
                    )}
                </Typography>


                <div className="sui-multi-checkbox-facet">
                    {options.length < 1 && <div>No matching options</div>}
                    {options.map(option => {
                        const checked = option.selected;
                        return (
                            <label
                                key={`${getFilterValueDisplay(option.value)}`}
                                htmlFor={`example_facet_${label}${getFilterValueDisplay(
                                    option.value
                                )}`}
                                className="sui-multi-checkbox-facet__option-label"
                            >
                                <Checkbox color="primary"
                                    id={`example_facet_${label}${getFilterValueDisplay(
                                        option.value
                                    )}`}
                                    checked={checked}
                                    onChange={() =>
                                        checked ? onRemove(option.value) : onSelect(option.value)
                                    }
                                />
                                <span className="sui-multi-checkbox-facet__input-text">
                                    {getFilterValueDisplay(option.value)}
                                </span>
                                <span className="sui-multi-checkbox-facet__option-count">
                                    {option.count.toLocaleString("en")}
                                </span>
                            </label>
                        );
                    })}
                </div>

                {showMore && (
                    <Button
                        type="button"
                        className="sui-facet-view-more"
                        onClick={onMoreClick}
                        aria-label="Show more options"
                    >
                        + More
                    </Button>
                )}
            </CardContent>
        </StyledCard>
    );
}
