import React from "react";
import AppSearchAPIConnector from "@elastic/search-ui-app-search-connector";
import SearchBoxMaterial from "../components/SearchBoxMaterial"
import ResultView from "../components/DomainResult"
import PagingView, { setCurrent } from "../components/PagingView"
import PacmanLoader from "react-spinners/ScaleLoader";
import Pagination from '@material-ui/lab/Pagination';
import {
    ErrorBoundary,
    Facet,
    SearchProvider,
    SearchBox,
    Results,
    PagingInfo,
    ResultsPerPage,
    Paging,
    Sorting,
    WithSearch
} from "@elastic/react-search-ui";
import CssBaseline from '@material-ui/core/CssBaseline';
import { Layout } from "@elastic/react-search-ui-views";
//import "@elastic/react-search-ui-views/lib/styles/styles.css";
import {
    buildAutocompleteQueryConfig,
    buildFacetConfigFromConfig,
    buildSearchOptionsFromConfig,
    buildSortOptionsFromConfig,
    getConfig,
    getFacetFields
} from "../config/config-helper";
import LinearProgress from '@material-ui/core/LinearProgress';
const { hostIdentifier, searchKey, endpointBase, engineName } = getConfig();
const connector = new AppSearchAPIConnector({
    searchKey,
    engineName,
    hostIdentifier,
    endpointBase
});
const config = {
    searchQuery: {
        facets: buildFacetConfigFromConfig(),
        ...buildSearchOptionsFromConfig()
    },
    autocompleteQuery: buildAutocompleteQueryConfig(),
    apiConnector: connector,
    alwaysSearchOnInitialLoad: true,
    initialState: {
        resultsPerPage: 5
    }
};

export default function Home() {
    return (
        <React.Fragment>
            <CssBaseline />
            <SearchProvider config={config}  >

                <WithSearch mapContextToProps={({ wasSearched, isLoading }) => ({ wasSearched, isLoading })}>
                    {({ wasSearched, isLoading }) => {
                        return (
                            <div className="App">
                                <ErrorBoundary>
                                    <Layout
                                        header={
                                            //<SearchBox autocompleteSuggestions={true} searchAsYouType={false} autocompleteMinimumCharacters={3} />
                                            //<CustomizedInputBase></CustomizedInputBase>

                                            // <SearchBox
                                            //     autocompleteSuggestions={true}
                                            //     inputView={({ getAutocomplete, getInputProps, getButtonProps }) => (
                                            //         <>
                                            //             <SearchBar
                                            //                 onRequestSearch={() => console.log('onRequestSearch')}
                                            //                 style={{
                                            //                     margin: '0 auto',
                                            //                     maxWidth: 800
                                            //                 }}
                                            //             />
                                            //             {getAutocomplete()}
                                            //         </>
                                            //     )}
                                            // />
                                            // <SearchBox
                                            //     autocompleteView={({ autocompletedResults, getItemProps }) => (
                                            //         <div className="sui-search-box__autocomplete-container">
                                            //             {autocompletedResults.map((result, i) => ( 
                                            //                 <div
                                            //                     {...getItemProps({
                                            //                         key: result.id.raw,
                                            //                         item: result
                                            //                     })}
                                            //                 >
                                            //                     Result {i}: {result.title.snippet} 
                                            //                 </div>
                                            //             ))}
                                            //         </div>
                                            //     )}
                                            // />
                                            // <SearchBox
                                            //     autocompleteSuggestions={true}
                                            //     inputView={({ getAutocomplete, getInputProps, getButtonProps }) => (
                                            //         <>
                                            //         <div className="sui-search-box__wrapper">
                                            //                 <Input {...getInputProps({ placeholder: 'Search games' })} />
                                            //             {getAutocomplete()}
                                            //         </div>
                                            //         <button {...getButtonProps()}>
                                            //             <svg viewBox="0 0 250 250" width="20" height="20" role="img">
                                            //             <title>Search</title>
                                            //             <path d="M244.186 214.604l-54.379-54.378c-.289-.289-.628-.491-.93-.76 10.7-16.231 16.945-35.66 16.945-56.554C205.822 46.075 159.747 0 102.911 0S0 46.075 0 102.911c0 56.835 46.074 102.911 102.91 102.911 20.895 0 40.323-6.245 56.554-16.945.269.301.47.64.759.929l54.38 54.38c8.169 8.168 21.413 8.168 29.583 0 8.168-8.169 8.168-21.413 0-29.582zm-141.275-44.458c-37.134 0-67.236-30.102-67.236-67.235 0-37.134 30.103-67.236 67.236-67.236 37.132 0 67.235 30.103 67.235 67.236s-30.103 67.235-67.235 67.235z" />
                                            //             </svg>
                                            //         </button>
                                            //         </>
                                            //     )}
                                            //     />
                                            <>
                                                <SearchBoxMaterial></SearchBoxMaterial>

                                                {isLoading && (
                                                    <LinearProgress />
                                                )}
                                            </>
                                            // <SearchBox
                                            // autocompleteSuggestions={true}
                                            // inputView={({ getAutocomplete, getInputProps, getButtonProps }) => (
                                            //     <>
                                            //         <Autocomplete
                                            //             id="free-solo-demo"
                                            //             freeSolo
                                            //             options={getAutocomplete()}
                                            //             renderInput={(getInputProps) => (
                                            //                 <TextField {...getInputProps} label="freeSolo" margin="normal" variant="outlined" />
                                            //             )}
                                            //         />
                                            //         <TextField {...getInputProps({ placeholder: 'Search games' })} label="freeSolo" margin="normal" variant="outlined" />
                                            //     </>
                                            // )}
                                            // />
                                        }
                                        sideContent={
                                            <div>
                                                {!isLoading && wasSearched && (
                                                    <Sorting
                                                        label={"Sort by"}
                                                        sortOptions={buildSortOptionsFromConfig()}
                                                    />
                                                )}
                                                {getFacetFields().map(field => (
                                                    <Facet key={field} field={field} label={field} isFilterable={true} />
                                                ))}
                                            </div>
                                        }
                                        bodyContent={
                                            <div>

                                                {isLoading && (
                                                    <LinearProgress />
                                                )}
                                                {wasSearched && (
                                                    <Results resultView={ResultView}
                                                    />

                                                )}
                                            </div>
                                        }
                                        bodyHeader={
                                            <React.Fragment>
                                                {wasSearched && <PagingInfo />}
                                                {wasSearched && <ResultsPerPage options={[5, 10, 15]} />}
                                            </React.Fragment>
                                        }
                                        bodyFooter={
                                            <>
                                                <Paging view={PagingView} />
                                                <Paging
                                                    view={({ totalPages, onChange, current}) => (
                                                        <div className="paging-info">
                                                            <Pagination count={totalPages} color="primary" onChange={setCurrent} />
                                                        </div>

                                                    )}
                                                />
                                            </>
                                        }
                                    />
                                </ErrorBoundary>
                            </div>
                        );
                    }}
                </WithSearch>
            </SearchProvider>
        </React.Fragment>
    );
}
