import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import InputBase from '@material-ui/core/InputBase';
import Divider from '@material-ui/core/Divider';
import IconButton from '@material-ui/core/IconButton';
import MenuIcon from '@material-ui/icons/Menu';
import SearchIcon from '@material-ui/icons/Search';
import DirectionsIcon from '@material-ui/icons/Directions';

import {
    ErrorBoundary,
    Facet,
    SearchProvider,
    SearchBox,
    Results,
    PagingInfo,
    ResultsPerPage,
    Paging,
    Sorting,
    WithSearch
} from "@elastic/react-search-ui";

const useStyles = makeStyles((theme) => ({
    root: {
        padding: '2px 4px',
        display: 'flex',
        alignItems: 'center',
    },
    input: {
        marginLeft: theme.spacing(1),
        flex: 1,
    },
    iconButton: {
        padding: 10,
    },
}));

export default function CustomizedInputBase() {
    const classes = useStyles();

    return (

            <Paper component="form" className={classes.root}>
            <SearchBox
                view={({ onChange, value, onSubmit }) => (
                    <form onSubmit={onSubmit}>
                        <InputBase value={value} onChange={e => onChange(e.currentTarget.value)} />
                    </form>
                )}
            />
            {/* <SearchBox view={({ onChange, value, onSubmit }) => (
                <>
                <InputBase
                    className={classes.input}
                    placeholder="Search Zonefile"
                    inputProps={{ 'aria-label': 'search google maps' }}
                    value={value}
                />
                    <IconButton type="submit" onSubmit={onSubmit} className={classes.iconButton} aria-label="search">
                    <SearchIcon />
                </IconButton>
                </>
            )}
            /> */}
            </Paper>

    );
}
